import React, { useEffect, useState } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import InfiniteScroll from "react-infinite-scroll-component";

const News = (props)=> {

  const [articles, setArticles] = useState([])
  const [loading, setLoading] = useState(true)
  const [page, setPage] = useState(1)
  const [totalResults, setTotalResults] = useState(0)

  
  const capitalize = (string)=>{
    return string.charAt(0).toUpperCase() + string.slice(1)
  }


  const updateNews = async()=> {
    props.setProgress(10);
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=${  props.apiKey}&page=${page}&pageSize=${props.pageSize}`;
     setLoading (true)
    let data = await fetch(url);
      props.setProgress(30);
    let parsData = await data.json();
      props.setProgress(70);
      setArticles(parsData.articles)
      setTotalResults(parsData.totalResults)
      setLoading(false)
      props.setProgress(100);
  }

  useEffect(() => {
      document.title = `${capitalize(  props.category)}- Dawn-News`
    updateNews(); 
    // eslint-disable-next-line
}, [])

  const fetchMoreData = async() => {
    let url = `https://newsapi.org/v2/top-headlines?country=${  props.country}&category=${  props.category}&apiKey=${  props.apiKey}&page=${page + 1}&pageSize=${  props.pageSize}`;
    setPage(page + 1)
   let data = await fetch(url);
   let parsData = await data.json();
   setArticles(articles.concat(parsData.articles))
   setTotalResults(parsData.totalResults)
  };

    return (
      <div className="container my-3">
        <h1 className="text-center" style={{ margin: "35px", marginTop : '90px' }}>
          Daily News - Top {capitalize(  props.category)} Headlines
        </h1>
        {loading && <Spinner />}
        <InfiniteScroll
          dataLength={articles.length}
          next={fetchMoreData}
          hasMore={articles.length !== totalResults}
          loader={<Spinner/>}
        >
<div className="container">
<div className="row">
          { articles.map((element) => {
              return (
                <div className="col md-4" key={element.url}>
                  <NewsItem
                    title={element.title ? element.title : ""}
                    description={element.description ? element.description : ""}
                    imageUrl={
                      !element.urlToImage
                        ? "data:image/png;base64,"
                        : element.urlToImage
                    }
                    newsUrl={element.url}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source.name}
                  />
                </div>
              );
            })}
        </div>
</div>
</InfiniteScroll>
      
      </div>
    );
}

News.defaultProps = {
  country: "in",
  pageSize: 5,
  category: "general",
};

News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string,
};


export default News;
